mapper.select:
	select c.board_no boardNo, board_category boardCategory,c.b_user_id bUserId, c.board_subject boardSubject, c.b_school_name bSchoolName, c.b_view_count bViewCount, 
    decode(to_char(c.b_postdate ,'yymmdd'),
                    to_char(sysdate,'yymmdd'), to_char(c.b_postdate,'hh24:mi:ss'),
                    to_char(c.b_postdate ,'yy.mm.dd')) bPostdate, 
		    c.region region
		from
		    (
		    select rownum r, total.* 
		    from
		        (
		        select board_no,board_category, b_user_id, board_subject, b_school_name, b_view_count, b_postdate, region
		            from board
		            where board_category = #{boardCategory}
		             
		            	
		            		and board_subject like '%'||#{word}||'%'
		            	
		            	
		            		and b.${key} = #{word}
		            	
		            
		            order by board_no DESC
		            ) total
		        where rownum <= #{end}
		    )c
		where c.r > #{start}
