mapper.select:
select count(*)
from member
where user_id = #{id}

mapper.update:
  		update member
		set h_school_code = (select school_code 
		                     from h_school
		                     where school_name = #{highSchool}),
		    h_school_cate = #{hSchoolCate}
		where user_id = #{registerId}

  		update member
		set u_school_code = (select code 
		                     from u_school
		                     where name=#{university}),
		    u_school_cate= #{uSchoolCate}
		where user_id = #{registerId}
